import axios from 'axios';

const api = axios.create({
  baseURL: 'https//localhost/tarefa', // substitua com a URL da sua API
});

export const getTarefas = () => api.get('/');
export const getTarefa = (id) => api.get(`/${id}`);
export const createTarefa = (tarefa) => api.post('/', tarefa);
export const updateTarefa = (id, tarefa) => api.put(`/${id}`, tarefa);
export const deleteTarefa = (id) => api.delete(`/${id}`);
